from testversion import gen_win_conditions, sortlist
from testversion import creategrid

def test_winconds():
    assert gen_win_conditions(3,3) == [[0, 1, 2], [3, 4, 5], [0, 3, 6], [1, 4, 7], [2, 5, 8], [0, 4, 8], [2, 4, 6]]

def test_sortlist():
    assert sortlist([20,3,1,3,4,5]) == [1,3,4,5,20]

